import requestsimport jsonurl = "http://fanyi.baidu.com/basetrans"query_str = input("请输入要翻译的英文：")data = {"from": "en",        "to": "zh",        "query": query_str }headers = {"User-Agent": "Mozilla/5.0 (Linux; Android 5.0; SM-G900P Build/LRX21T) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/62.0.3202.75 Mobile Safari/537.36"}response = requests.post(url, data=data, headers=headers)#print(response)#response.encoding = "unicode_escape"#print(response.text)response_str = response.content.decode('unicode_escape') #json字符串response_dict = json.loads(response_str)print(response_dict)result = response_dict["trans"][0]["dst"]print("翻译结果是: {0}".format(result))