import requestsfrom retrying import retry'''专门请求url地址的方法'''#headers = {"User-Agent": "Mozilla/5.0 (Linux; Android 5.0; SM-G900P Build/LRX21T) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/62.0.3202.75 Mobile Safari/537.36"}headers = {"User-Agent": "Mozilla/5.0 (Linux; Android 5.0; SM-G900P Build/LRX21T) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/62.0.3202.75 Mobile Safari/537.36",           "referer": "https://m.douban.com/movie/watchonline"}@retry(stop_max_attempt_number=3)   #让被装饰的桉树反复执行三次，三次全部报错才会报错def _parse_url(url):    print("#"*100)    response = requests.get(url,headers=headers,timeout=5)    return response.content.decode()def parse_url(url):    try:        html_str = _parse_url(url)    except:        html_str = None    return html_strif __name__ == "__main__":    url = "http://www.baidu.com"    url1 = "www.baidu.com"    #不写协议会报错，以此来测试报错时是否尝试了stop_max_attempt_number次    print(parse_url(url1))